{% block body %}
{% if app.user %}

    <style>
        .custom-file-input{
            opacity: .4;
        }
    </style>

    <div class=\"container-fluid\">
        <div class=\"row\">
            <div class=\"col-xl-10 col-lg-9 col-md-8 ml-auto mb-5 mt-5\">

                {% include \"editor/apprenant/_nav_apprenant.html.twig\" with {'p': 1} %}
                
                <div class=\"my-5\">

                    <!-- afficher le formulaire d'ajout d'un apprenant -->
                    
                        <h4 class=\"btn btn-info btn-sm\">Cr√©er un apprenant</h4>
                        {{ form_start(form) }}
                        {{ form_widget(form) }}
                        
                        <button class=\"btn btn-outline-danger d-block ml-auto\">Enregistrer</button>
                        {{ form_end(form) }}
                </div>      
            </div>
        </div>
    </div>

{% endif %}
{% endblock %}



{% block javascripts %}

    {# ins√©rer les sous formulaires pour ajouter les r√©seaux sociaux #}
    <script src=\"{{asset('assets/js/collection.js')}}\"></script>

{% endblock %}

